sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */

    data.imtIncidents = [];
    var gr = new GlideRecord('sn_imt_imt_incidents');
    gr.addQuery('active', true);
    gr.orderBy('start_date');
    gr.query();
    while (gr.next()) {
        var temp = {};
        temp.sys_id = gr.sys_id.toString();
        temp.name = gr.name.getDisplayValue();
        temp.type = gr.type.getDisplayValue();
        temp.start_date = gr.getDisplayValue('start_date');
        temp.end_date = gr.getDisplayValue('end_date');
        data.imtIncidents.push(temp);
    }
})();
