sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    // gs.info("TT Duration BR running");
    // Add your code here

    var gdt1 = new GlideDateTime(current.check_in);
    var gdt2 = new GlideDateTime(current.lunch_out);
    var gdt3 = new GlideDateTime(current.lunch_in);
    var gdt4 = new GlideDateTime(current.check_out);	

    var durRecord = GlideDateTime.subtract(gdt1, gdt4); //the difference between gdt1 and gdt2
	var durRecordLunch = GlideDateTime.subtract(gdt2, gdt3); //the difference between gdt2 and gdt3

	var durVal = durRecord.getDisplayValue();
	var durValLunch = durRecordLunch.getDisplayValue();	


	var durLunch = durRecordLunch.getNumericValue();
    var durationHoursLunch = (durLunch / (1000 * 60 * 60)).toFixed(2);

    var dur = durRecord.getNumericValue();
    var durationHours = (dur / (1000 * 60 * 60)).toFixed(2);

	current.duration = durationHours - durationHoursLunch;
    current.update();


    var timeWorked = 0;
    var trGr = new GlideRecord('x_snc_time_trackin_time_tracker');
    trGr.addQuery('time_card_daily', current.time_card_daily);
    //trGr.addQuery('type', '!=', 'Lunch');
    //trGr.addQuery('type', '!=', 'Off');
    trGr.query();
    while (trGr.next()) {
        timeWorked = parseFloat(timeWorked) + parseFloat(trGr.getDisplayValue('duration'));

        var gr = new GlideRecord('time_card_daily');
        gr.addQuery('sys_id', current.time_card_daily);
        gr.query();
        if (gr.next()) {
            gr.time_worked = timeWorked;
            gr.update();
        }
    }
})(current, previous);
