sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    gs.info("TT Duration BR running");
    // Add your code here

    var gdt1 = new GlideDateTime(current.check_in);
    var gdt2 = new GlideDateTime(current.check_out);

    var durRecord = GlideDateTime.subtract(gdt1, gdt2); //the difference between gdt1 and gdt2

    var durVal = durRecord.getDisplayValue();
    // gs.addInfoMessage("Duration value" + durVal);
    var dur = durRecord.getNumericValue();
    var durationHours = (dur / (1000 * 60 * 60)).toFixed(2);
    current.duration = durationHours;
    current.update();
    // gs.info(dur);
    //gs.info(durationHours);

	//gs.addInfoMessage("CUR " + current.time_card_daily);

    var timeWorked = 0;
    var trGr = new GlideRecord('x_snc_time_trackin_time_tracker');
    trGr.addQuery('time_card_daily', current.time_card_daily);
	trGr.addQuery('type','!=','Lunch');
    trGr.query();
	//gs.addInfoMessage("ROW COUNT "+trGr.getRowCount());
    while (trGr.next()) {

            //var addedtime = parseFloat(gr.getValue('time_worked')) + parseFloat(durationHours);

            timeWorked = parseFloat(timeWorked) + parseFloat(trGr.getDisplayValue('duration'));
		//gs.addInfoMessage("timew " + trGr.getDisplayValue('duration'));

    }

	//gs.addInfoMessage("total " + timeWorked);

    var gr = new GlideRecord('time_card_daily');
    gr.addQuery('sys_id', current.time_card_daily);
    gr.query();
    //gs.addInfoMessage("row count"+gr.getRowCount());
    if (gr.next()) {
            gr.time_worked = timeWorked;
            gr.update();

        //gs.addInfoMessage(gr.getDisplayValue('time_worked') + durationHours);

    }





})(current, previous);
