sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    //current.time_card_daily
    //	time_card_daily

    var gr = new GlideRecord('time_card_daily');
    gr.addQuery('u_active', true);
    gr.query();

    while (gr.next()) {

        var gdt = new GlideDateTime(gr.date);
        var month = gdt.getMonthUTC();
        var dayofmonth = gdt.getDayOfMonthUTC();
        var payPeriod = (dayofmonth <= 15) ? (month * 2) - 1 : month * 2;
        payPeriod = payPeriod.toString();

        var gdt2 = new GlideDateTime();
        var month2 = gdt2.getMonthUTC();
        var dayofmonth2 = gdt2.getDayOfMonthUTC();
        var payPeriod2 = (dayofmonth2 <= 15) ? (month2 * 2) - 1 : month2 * 2;
        payPeriod2 = payPeriod2.toString();

        if (payPeriod > payPeriod2) {
            gr.u_status = "Locked";
            gr.update();
        }
    }
})(current, previous);
