sys_script_include.script:
var TimeTrackerUtil = Class.create();
TimeTrackerUtil.prototype = {
    initialize: function() {
    },
	removeRole: function(user, role) {
        var useRoleGr = new GlideRecord('sys_user_has_role');
        useRoleGr.addQuery("role", role);
        useRoleGr.addQuery("user", user);
        useRoleGr.query();
        if (useRoleGr.next()) {
            useRoleGr.state = 'pending_approval';
            useRoleGr.update();
        }
    },

    assignRole: function(user, role) {
        var useRoleGr = new GlideRecord('sys_user_has_role');
        useRoleGr.addQuery("role", role);
        useRoleGr.addQuery("user", user);
        useRoleGr.query();
        if (useRoleGr.next()) {
            useRoleGr.state = 'active';
            useRoleGr.update();
            return;
        }
        this._insertUserRole(user, role);

    },

    _insertUserRole: function(user, role) {
        var useRoleGr = new GlideRecord('sys_user_has_role');
        useRoleGr.initialize();
        useRoleGr.role = role;
        useRoleGr.user = user;
        useRoleGr.insert();
    },

    hasRole: function(roles) {
        return gs.getUser().hasRole(roles);
    },


    type: 'TimeTrackerUtil'
};
