sysauto_script.script:
//query x_snc_time_trackin_time_tracker
//look for parent record = to sysid of time_card_daily
//if time_tracker record exists don't send, if it doesnt exist, send event
var grd = new GlideRecord('time_card_daily');
grd.addEncodedQuery('dateONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()');
grd.query();
while (grd.next()) {
    var gr = new GlideRecord('x_snc_time_trackin_time_tracker');
    //gr.addEncodedQuery('time_card_daily.dateONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()');
    gr.addEncodedQuery('time_card_daily=' + grd.sys_id);
	gr.query();

    if (!gr.next()) {
        gs.eventQueue("x_snc_time_trackin.worker.hours.elapsed", grd, grd.u_user);
    }
}
